export declare const hdl = "// This file is part of www.nand2tetris.org\n// and the book \"The Elements of Computing Systems\"\n// by Nisan and Schocken, MIT Press.\n// File name: projects/1/DMux4Way.hdl\n/**\n * 4-way demultiplexor:\n * [a, b, c, d] = [in, 0, 0, 0] if sel = 00\n *                [0, in, 0, 0] if sel = 01\n *                [0, 0, in, 0] if sel = 10\n *                [0, 0, 0, in] if sel = 11\n */\nCHIP DMux4Way {\n    IN in, sel[2];\n    OUT a, b, c, d;\n\n    PARTS:\n    //// Replace this comment with your code.\n}";
export declare const tst = "// This file is part of www.nand2tetris.org\n// and the book \"The Elements of Computing Systems\"\n// by Nisan and Schocken, MIT Press.\n// File name: projects/1/DMux4Way.tst\n\nload DMux4Way.hdl,\ncompare-to DMux4Way.cmp,\noutput-list in sel%B2.2.2 a b c d;\n\nset in 0,\nset sel %B00,\neval,\noutput;\n\nset sel %B01,\neval,\noutput;\n\nset sel %B10,\neval,\noutput;\n\nset sel %B11,\neval,\noutput;\n\nset in 1,\nset sel %B00,\neval,\noutput;\n\nset sel %B01,\neval,\noutput;\n\nset sel %B10,\neval,\noutput;\n\nset sel %B11,\neval,\noutput;";
export declare const cmp = "|in | sel  | a | b | c | d |\n| 0 |  00  | 0 | 0 | 0 | 0 |\n| 0 |  01  | 0 | 0 | 0 | 0 |\n| 0 |  10  | 0 | 0 | 0 | 0 |\n| 0 |  11  | 0 | 0 | 0 | 0 |\n| 1 |  00  | 1 | 0 | 0 | 0 |\n| 1 |  01  | 0 | 1 | 0 | 0 |\n| 1 |  10  | 0 | 0 | 1 | 0 |\n| 1 |  11  | 0 | 0 | 0 | 1 |";
