export declare const vm = "// This file is part of www.nand2tetris.org\n// and the book \"The Elements of Computing Systems\"\n// by Nisan and Schocken, MIT Press.\n// File name: projects/7/MemoryAccess/BasicTest/BasicTest.vm\n\n// Executes pop and push commands.\n\npush constant 10\npop local 0\npush constant 21\npush constant 22\npop argument 2\npop argument 1\npush constant 36\npop this 6\npush constant 42\npush constant 45\npop that 5\npop that 2\npush constant 510\npop temp 6\npush local 0\npush that 5\nadd\npush argument 1\nsub\npush this 6\npush this 6\nadd\nsub\npush temp 6\nadd\n";
export declare const vm_tst = "// This file is part of www.nand2tetris.org\n// and the book \"The Elements of Computing Systems\"\n// by Nisan and Schocken, MIT Press.\n// File name: projects/7/MemoryAccess/BasicTest/BasicTestVME.tst\n\n// Tests and illustrates BasicTest.vm on the VM simulator.\n// Starts by setting the stack pointer and the base addresses\n// of relevant memory segments to selected RAM addresses.\n\nload BasicTest.vm,\ncompare-to BasicTest.cmp,\n\nset sp 256,        // stack pointer\nset local 300,     // base address of the local segment\nset argument 400,  // base address of the argument segment\nset this 3000,     // base address of the this segment\nset that 3010,     // base address of the that segment\n\nrepeat 25 {        // BasicTest.vm has 25 VM commands\n  vmstep;\n}\n\n// Outputs the value at the stack's base and some values from the tested memory segments\noutput-list RAM[256]%D1.6.1 RAM[300]%D1.6.1 RAM[401]%D1.6.1 \n            RAM[402]%D1.6.1 RAM[3006]%D1.6.1 RAM[3012]%D1.6.1\n            RAM[3015]%D1.6.1 RAM[11]%D1.6.1;\noutput;\n";
export declare const hdl_tst = "// This file is part of www.nand2tetris.org\n// and the book \"The Elements of Computing Systems\"\n// by Nisan and Schocken, MIT Press.\n// File name: projects/7/MemoryAccess/BasicTest/BasicTest.tst\n\n// Tests BasicTest.asm on the CPU emulator.\n\ncompare-to BasicTest.cmp,\n\nset RAM[0] 256,   // stack pointer\nset RAM[1] 300,   // base address of the local segment\nset RAM[2] 400,   // base address of the argument segment\nset RAM[3] 3000,  // base address of the this segment\nset RAM[4] 3010,  // base address of the that segment\n\nrepeat 600 {      // enough cycles to complete the execution\n  ticktock;\n}\n\n// Outputs the value at the stack's base and some values from the tested memory segments\noutput-list RAM[256]%D1.6.1 RAM[300]%D1.6.1 RAM[401]%D1.6.1 \n            RAM[402]%D1.6.1 RAM[3006]%D1.6.1 RAM[3012]%D1.6.1\n            RAM[3015]%D1.6.1 RAM[11]%D1.6.1;\noutput;\n";
export declare const cmp = "|RAM[256]|RAM[300]|RAM[401]|RAM[402]|RAM[3006|RAM[3012|RAM[3015|RAM[11] |\n|    472 |     10 |     21 |     22 |     36 |     42 |     45 |    510 |";
