export declare const sys = "// Sys.vm. Tested by the NestedCall test script.\n// Consists of three functions: Sys.init, Sys.main, and Sys.add12.\n\n// Calls Sys.main() and stores a return value in temp 1.\n// Does not return (enters infinite loop).\n// The VM implementation starts running the Sys.init function, by default.\nfunction Sys.init 0\n\tpush constant 4000\t// tests that THIS and THAT are handled correctly\n\tpop pointer 0\n\tpush constant 5000\n\tpop pointer 1\n\tcall Sys.main 0\n\tpop temp 1\n\tlabel LOOP\n\tgoto LOOP\n\n// Sets locals 1, 2 and 3 to some values. Leaves locals 0 and 4 unchanged, \n// to test that the 'function' VM command initializes them to 0 (the test \n// script sets them to -1 before this code starts running).\n// Calls Sys.add12(123) and stores the return value (should be 135) in temp 0.\n// Returns local 0 + local 1 + local 2 + local 3 + local 4 (should be 456), to \n// confirm that locals were not mangled by the function call.\nfunction Sys.main 5\n\tpush constant 4001\n\tpop pointer 0\n\tpush constant 5001\n\tpop pointer 1\n\tpush constant 200\n\tpop local 1\n\tpush constant 40\n\tpop local 2\n\tpush constant 6\n\tpop local 3\n\tpush constant 123\n\tcall Sys.add12 1\n\tpop temp 0\n\tpush local 0\n\tpush local 1\n\tpush local 2\n\tpush local 3\n\tpush local 4\n\tadd\n\tadd\n\tadd\n\tadd\n\treturn\n\n// Returns (argument 0) + 12.\nfunction Sys.add12 0\n\tpush constant 4002\n\tpop pointer 0\n\tpush constant 5002\n\tpop pointer 1\n\tpush argument 0\n\tpush constant 12\n\tadd\n\treturn\n";
export declare const vm_tst = "// Tests and illustrates how the VM implementation handles function-call-and-return,\n// by executing the functions in Sys.vm in the VM emulator.\n// In particular, loads and runs the functions in Sys.vm.\n\nload Sys.vm,\ncompare-to NestedCall.cmp,\noutput-list RAM[0]%D1.6.1 RAM[1]%D1.6.1 RAM[2]%D1.6.1 RAM[3]%D1.6.1 RAM[4]%D1.6.1 RAM[5]%D1.6.1 RAM[6]%D1.6.1;\n\nset RAM[0] 261,\nset RAM[1] 261,\nset RAM[2] 256,\nset RAM[3] -3,\nset RAM[4] -4,\nset RAM[5] -1, // test results\nset RAM[6] -1,\nset RAM[256] 1234, // fake stack frame from call Sys.init\nset RAM[257] -1,\nset RAM[258] -2,\nset RAM[259] -3,\nset RAM[260] -4,\n\nset RAM[261] -1, // Initialize stack to check for local segment\nset RAM[262] -1, // being cleared to zero.\nset RAM[263] -1,\nset RAM[264] -1,\nset RAM[265] -1,\nset RAM[266] -1,\nset RAM[267] -1,\nset RAM[268] -1,\nset RAM[269] -1,\nset RAM[270] -1,\nset RAM[271] -1,\nset RAM[272] -1,\nset RAM[273] -1,\nset RAM[274] -1,\nset RAM[275] -1,\nset RAM[276] -1,\nset RAM[277] -1,\nset RAM[278] -1,\nset RAM[279] -1,\nset RAM[280] -1,\nset RAM[281] -1,\nset RAM[282] -1,\nset RAM[283] -1,\nset RAM[284] -1,\nset RAM[285] -1,\nset RAM[286] -1,\nset RAM[287] -1,\nset RAM[288] -1,\nset RAM[289] -1,\nset RAM[290] -1,\nset RAM[291] -1,\nset RAM[292] -1,\nset RAM[293] -1,\nset RAM[294] -1,\nset RAM[295] -1,\nset RAM[296] -1,\nset RAM[297] -1,\nset RAM[298] -1,\nset RAM[299] -1,\n\nset sp 261,\nset local 261,\nset argument 256,\nset this 3000,\nset that 4000;\n\nrepeat 50 {\n\tvmstep;\n}\noutput;\n";
export declare const hdl_tst = "// Tests how the VM implementation handles function-call-and-return,\n// by executing the functions in Sys.vm.\n// In particular, loads and runs NestedCall.asm, which results when \n// the VM translator is applied to the NestedCall folder, which \n// includes only one VM file: Sys.vm.\n\ncompare-to NestedCall.cmp,\n\nset RAM[0] 261,\nset RAM[1] 261,\nset RAM[2] 256,\nset RAM[3] -3,\nset RAM[4] -4,\nset RAM[5] -1,     // test results\nset RAM[6] -1,\nset RAM[256] 1234, // fake stack frame from call Sys.init\nset RAM[257] -1,\nset RAM[258] -2,\nset RAM[259] -3,\nset RAM[260] -4,\n\nset RAM[261] -1,   // Initializes the stack, to check that the local segment\nset RAM[262] -1,   // is initialized to zeros by the 'function' VM command.\nset RAM[263] -1,\nset RAM[264] -1,\nset RAM[265] -1,\nset RAM[266] -1,\nset RAM[267] -1,\nset RAM[268] -1,\nset RAM[269] -1,\nset RAM[270] -1,\nset RAM[271] -1,\nset RAM[272] -1,\nset RAM[273] -1,\nset RAM[274] -1,\nset RAM[275] -1,\nset RAM[276] -1,\nset RAM[277] -1,\nset RAM[278] -1,\nset RAM[279] -1,\nset RAM[280] -1,\nset RAM[281] -1,\nset RAM[282] -1,\nset RAM[283] -1,\nset RAM[284] -1,\nset RAM[285] -1,\nset RAM[286] -1,\nset RAM[287] -1,\nset RAM[288] -1,\nset RAM[289] -1,\nset RAM[290] -1,\nset RAM[291] -1,\nset RAM[292] -1,\nset RAM[293] -1,\nset RAM[294] -1,\nset RAM[295] -1,\nset RAM[296] -1,\nset RAM[297] -1,\nset RAM[298] -1,\nset RAM[299] -1,\n\nrepeat 4000 {\n\tticktock;\n}\n\noutput-list RAM[0]%D1.6.1 RAM[1]%D1.6.1 RAM[2]%D1.6.1 RAM[3]%D1.6.1 RAM[4]%D1.6.1 RAM[5]%D1.6.1 RAM[6]%D1.6.1;\noutput;\n";
export declare const cmp = "| RAM[0] | RAM[1] | RAM[2] | RAM[3] | RAM[4] | RAM[5] | RAM[6] |\n|    261 |    261 |    256 |   4000 |   5000 |    135 |    246 |\n";
