export declare const FillAsm = "// Runs an infinite loop that listens to the keyboard input. \n// When a key is pressed (any key), the program blackens the screen,\n// i.e. writes \"black\" in every pixel. When no key is pressed, \n// the screen should be cleared.\n\n// initialize variables\n(START)\t\t\t// while (true)\n\t@SCREEN\n\tD=A\n\t@i\n\tM=D\t\t// i = SCREEN\n\n(LOOP)\t\t\t// while (i < screen - 1)\n\t@i\n\tD=M\n\t@24575\t\t// screen - 1\n\tD=A-D\n\t@START\n\tD;JLT\t\t// i < screen - 1 (negative check)\n\t@KBD\n\tD=M\n\t@BLACKEN\t// if (kbd != 0)\n\tD;JNE\n\n// Clear the screen (write \"white\" in every pixel)\n\t@i\n\tD=M\n\tA=D\n\tM=0\t\t// write \"white\"\n\t@CONTINUE\n\t0;JMP\n\n(BLACKEN)\n\t@i\n\tD=M\n\tA=D\n\tM=-1\t\t// write \"black\"\n\n(CONTINUE)\n\t@i\n\tM=M+1\t\t// i++\n\t@LOOP\n\t0;JMP";
export declare const FillHack: number[];
